import React, { Component } from 'react';
import Slider from 'react-slick';

class PhotoSlider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      photos: [
        {
          url: 'argile.jpg',
          caption: 'Image 1'
        },
        {
          url: 'huile-nuit.jpg',
          caption: 'Image 2'
        },
        {
          url: 'image3.jpg',
          caption: 'Image 3'
        }
      ]
    }
  }

  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };

    return (
      <div className="photo-slider">
        <Slider {...settings}>
          {this.state.photos.map((photo, index) => (
            <div key={index}>
              <img src={photo.url} alt={photo.caption} />
              <p>{photo.caption}</p>
            </div>
          ))}
        </Slider>
      </div>
    );
  }
}

export default PhotoSlider;
